custom_css:
.countdown-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  padding: 10px 15px;
  background: linear-gradient(90deg, #ff6b6b, #ee5a5a);
  color: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
  text-align: center;
}

.countdown-container h3 {
  margin: 0;
  font-size: 14px;
  font-weight: 600;
}

.countdown-timer {
  display: flex;
  align-items: center;
  gap: 6px;
  animation: pulse 2s infinite ease-in-out;
}

.countdown-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(0, 0, 0, 0.2);
  padding: 5px 8px;
  border-radius: 4px;
}

.countdown-value {
  font-size: 18px;
  font-weight: bold;
  font-variant-numeric: tabular-nums;
}

.countdown-label {
  font-size: 9px;
  text-transform: uppercase;
  opacity: 0.9;
}

.countdown-separator {
  font-size: 18px;
  font-weight: bold;
}

@keyframes pulse {
  0%,
  100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
}

custom_js:
(function() {
  const endTime = new Date().getTime() + (2 * 60 * 60 * 1000);

  function updateCountdown() {
    const now = new Date().getTime();
    const distance = endTime - now;

    if (distance < 0) {
      const container = document.getElementById('topbar-countdown');
      if (container) container.innerHTML = '<span>Promotion ended!</span>';
      return;
    }

    const hours = Math.floor(distance / (1000 * 60 * 60));
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    const h = document.getElementById('countdown-hours');
    const m = document.getElementById('countdown-minutes');
    const s = document.getElementById('countdown-seconds');

    if (h) h.textContent = hours.toString().padStart(2, '0');
    if (m) m.textContent = minutes.toString().padStart(2, '0');
    if (s) s.textContent = seconds.toString().padStart(2, '0');
  }

  updateCountdown();
  setInterval(updateCountdown, 1000);
  console.log('Countdown timer initialized!');
})();
